(18, 1, 0, 'head', '', 0, 5, 0, '<head>\n    <title>{\'pagetitle\' | resource}</title>\n    <base href=\"{\'site_url\' | config}\">\n    <meta charset=\"{\'modx_charset\' | config}\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1, maximum-scale=1\">\n    \n    {if \'favicon\' | config}\n        <link rel=\"icon\" type=\"image/svg+xml\" href=\"{\'favicon\' | config}\">\n    {/if}\n    \n    <script src=\"/template/assets/libs/jquery/jquery.min.js\"></script>\n    \n    {\'!MinifyX\' | snippet : [\n        \'minifyCss\' => 1,\n        \'minifyJs\' => 1,\n        \'registerJs\' => \'default\',\n        \'registerCss\' => \'default\',\n        \'cssSources\' => \'\n            /template/assets/scss/main.scss,\n    		/template/assets/libs/fancybox/jquery.fancybox.min.css,\n    		/template/assets/libs/splide/splide-core.min.css\n        \',\n        \'jsSources\' => \'\n    	    /template/assets/libs/jquery-mask/jquery.mask.min.js,\n    	    /template/assets/libs/jquery-cookie/jquery.cookie.min.js,\n    	    /template/assets/libs/fancybox/jquery.fancybox.min.js,\n    	    /template/assets/libs/splide/splide.min.js,\n    	    /template/assets/js/components.js,\n    	    /template/assets/js/main.js\n        \'\n    ]}\n</head>', 0, 'a:0:{}', 0, ''),
