INSERT INTO `modx_site_htmlsnippets` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `static`, `static_file`) VALUES
(27, 1, 0, 'tpl.AjaxForm.example', '', 0, 8, 0, '<form action=\"\" method=\"post\" class=\"ajax_form af_example\">\n\n    <div class=\"form-group\">\n        <label class=\"control-label\" for=\"af_name\">[[%af_label_name]]</label>\n        <div class=\"controls\">\n            <input type=\"text\" id=\"af_name\" name=\"name\" value=\"[[+fi.name]]\" placeholder=\"\" class=\"form-control\"/>\n            <span class=\"error_name\">[[+fi.error.name]]</span>\n        </div>\n    </div>\n\n    <div class=\"form-group\">\n        <label class=\"control-label\" for=\"af_email\">[[%af_label_email]]</label>\n        <div class=\"controls\">\n            <input type=\"email\" id=\"af_email\" name=\"email\" value=\"[[+fi.email]]\" placeholder=\"\" class=\"form-control\"/>\n            <span class=\"error_email\">[[+fi.error.email]]</span>\n        </div>\n    </div>\n\n    <div class=\"form-group\">\n        <label class=\"control-label\" for=\"af_message\">[[%af_label_message]]</label>\n        <div class=\"controls\">\n            <textarea id=\"af_message\" name=\"message\" class=\"form-control\" rows=\"5\">[[+fi.message]]</textarea>\n            <span class=\"error_message\">[[+fi.error.message]]</span>\n        </div>\n    </div>\n\n    <div class=\"form-group\">\n        <div class=\"controls\">\n            <button type=\"reset\" class=\"btn btn-default\">[[%af_reset]]</button>\n            <button type=\"submit\" class=\"btn btn-primary\">[[%af_submit]]</button>\n        </div>\n    </div>\n\n    [[+fi.success:is=`1`:then=`\n    <div class=\"alert alert-success\">[[+fi.successMessage]]</div>\n    `]]\n    [[+fi.validation_error:is=`1`:then=`\n    <div class=\"alert alert-danger\">[[+fi.validation_error_message]]</div>\n    `]]\n</form>', 0, NULL, 0, 'core/components/ajaxform/elements/chunks/chunk.example.tpl'),
(28, 1, 0, 'tpl.login.form', '', 0, 9, 0, '<div id=\"container\">\r\n	<div id=\"modx-login-logo\">\r\n		<!--[if gte IE 9]><!--><img src=\"/manager/templates/default/images/modx-logo-color.svg\" alt=\"MODX CMS/CMF\"><!--<![endif]-->\r\n		<!--[if lt IE 9]><img alt=\"MODX CMS/CMF\" src=\"/manager/templates/default/images/modx-logo-color.png\" /><![endif]-->\r\n	</div>\r\n\r\n	<div class=\"modx-form\" id=\"modx-login-panel\">\r\n		<div class=\"panel-title\">\r\n			<h2>[[++site_name]]</h2>\r\n		</div>\r\n		<div id=\"errormsg\">[[+errormsg]]</div>\r\n		<div class=\"panel-body\">\r\n			<form method=\"post\" action=\"\" id=\"manager-login-form\">\r\n				<div class=\"login-form-item\">\r\n					<div class=\"login-form-element\">\r\n						<input type=\"text\" placeholder=\"[[%admintools_username_email]]\" class=\"form-text\" name=\"userdata\" id=\"userdata\" autocomplete=\"on\">\r\n					</div>\r\n				</div>\r\n				<div class=\"form-action\">\r\n					<button id=\"send-email-btn\" value=\"1\" type=\"submit\" class=\"primary-button login-form-btn\" data-sending=\"[[%admintools_sending]]\">[[%admintools_send_authorization_link]]</button>\r\n				</div>\r\n			</form>\r\n		</div>\r\n	</div>\r\n</div>', 0, NULL, 0, 'core/components/admintools/elements/chunks/chunk.login.form.tpl'),
(29, 1, 0, 'tpl.lockScreen', '', 0, 9, 0, '<!DOCTYPE html>\r\n<html lang=\"[[+lang]]\">\r\n<head>\r\n	<meta charset=\"UTF-8\">\r\n	<title>[[+title]]</title>\r\n	<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\" />\r\n	<style>\r\n		.lockscreen {\r\n			background-image: url([[+assets_url]]components/admintools/images/lockscreen.jpg);\r\n		}\r\n		@media only screen and (max-width : 767px) {\r\n			.lockscreen {\r\n				background-image: url([[+assets_url]]components/admintools/images/lockscreen800.jpg);\r\n			}\r\n		}\r\n		@media only screen and (min-width : 768px) and (max-width: 1223px) {\r\n			.lockscreen {\r\n				background-image: url([[+assets_url]]components/admintools/images/lockscreen1280.jpg);\r\n			}\r\n		}\r\n		.lockscreen .container {\r\n			background-color: rgba(255,255,255,0.1);\r\n			margin: 5% auto 0;\r\n			padding: 20px;\r\n			text-align: center;\r\n			width: 250px;\r\n		}\r\n		.lockscreen .form-element {\r\n			box-sizing: border-box;\r\n			font-size: 14px;\r\n			margin: 5px 0;\r\n			padding: 6px 12px;\r\n			width: 220px;\r\n		}\r\n		.lockscreen .username {\r\n			font-size: 20px;\r\n			color: #fff;\r\n			padding: 10px;\r\n		}\r\n		.lockscreen input {\r\n			border: 1px solid transparent;\r\n		}\r\n		.lockscreen input:focus {\r\n			border-color: #45a2ec;\r\n			outline: 0;\r\n		}\r\n		.lockscreen .btn {\r\n			display: inline-block;\r\n			color: #fff;\r\n			background-color: #3697CD;\r\n			font-weight: bold;\r\n			line-height: 1.6;\r\n			text-align: center;\r\n			vertical-align: middle;\r\n			cursor: pointer;\r\n			border: 1px solid #3CA4DE;\r\n		}\r\n		.btn:hover {\r\n			color: #fff;\r\n			background-color: #286090;\r\n			border-color: #1e4c74;\r\n		}\r\n	</style>\r\n</head>\r\n<body class=\"lockscreen\">\r\n<div class=\"container\">\r\n	<div class=\"photo\"><img src=\"[[+photo]]\" alt=\"avatar\" onsubmit=\"return false;\"></div>\r\n	<div class=\"username\">[[+username]]</div>\r\n	<form name=\"unlockform\" action=\"[[+form_action]]\" method=\"POST\">\r\n		<div>\r\n			<input id=\"unlock_code\" class=\"form-element\" type=\"password\" name=\"unlock_code\" placeholder=\"[[+input_placeholder]]\" required>\r\n		</div>\r\n		<div>\r\n			<button class=\"form-element btn submit-btn\">[[%admintools_unlock]]</button>\r\n		</div>\r\n	</form>\r\n</div>\r\n<script>\r\n	document.forms[0].addEventListener(\"submit\", function(event) {\r\n		event.preventDefault();\r\n		let body = \"admintools_action=unlock&action=mgr/system/unlock&unlock_code=\" + encodeURIComponent(unlock_code.value) + \"&HTTP_MODAUTH=[[+auth]]\";\r\n		let xhr = new XMLHttpRequest();\r\n		xhr.open(this.getAttribute(\'method\'), this.getAttribute(\'action\'), true);\r\n		xhr.setRequestHeader(\"X-Requested-With\", \"XMLHttpRequest\");\r\n		xhr.setRequestHeader(\'Content-Type\', \'application/x-www-form-urlencoded\');\r\n		xhr.onreadystatechange = function() {\r\n			if (this.readyState == 4 && this.status == 200) {\r\n				document.location.reload();\r\n			}\r\n		};\r\n\r\n		xhr.send(body);\r\n	});\r\n</script>\r\n</body>\r\n</html>', 0, NULL, 0, 'core/components/admintools/elements/chunks/chunk.lockscreen.tpl'),
